import React, { useState } from 'react';

const TrackComplaint = () => {
  const [trackingId, setTrackingId] = useState('');
  const [complaint, setComplaint] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically fetch the complaint data from your backend
    // For now, we'll simulate a response
    if (trackingId) {
      // Simulated complaint data
      setComplaint({
        id: trackingId,
        status: 'Under Review',
        submittedDate: new Date().toLocaleDateString(),
        department: 'IT',
        subject: 'Sample Complaint',
        description: 'This is a sample complaint description.'
      });
      setError('');
    } else {
      setError('Please enter a tracking ID');
    }
  };

  return (
    <div className="content">
      <div className="form-container">
        <h1 className="form-title">Track Your Complaint</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label" htmlFor="trackingId">Tracking ID</label>
            <input
              type="text"
              id="trackingId"
              className="form-input"
              value={trackingId}
              onChange={(e) => setTrackingId(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="form-button">
            Track Complaint
          </button>
        </form>

        {error && (
          <div style={{ color: 'red', marginTop: '1rem', textAlign: 'center' }}>
            {error}
          </div>
        )}

        {complaint && (
          <div className="table-container" style={{ marginTop: '2rem' }}>
            <table>
              <tbody>
                <tr>
                  <th>Tracking ID</th>
                  <td>{complaint.id}</td>
                </tr>
                <tr>
                  <th>Status</th>
                  <td className={`status-${complaint.status.toLowerCase().replace(' ', '')}`}>
                    {complaint.status}
                  </td>
                </tr>
                <tr>
                  <th>Submitted Date</th>
                  <td>{complaint.submittedDate}</td>
                </tr>
                <tr>
                  <th>Department</th>
                  <td>{complaint.department}</td>
                </tr>
                <tr>
                  <th>Subject</th>
                  <td>{complaint.subject}</td>
                </tr>
                <tr>
                  <th>Description</th>
                  <td>{complaint.description}</td>
                </tr>
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default TrackComplaint;
